mapper.update:
UPDATE t_article
SET
hits=#{hits,jdbcType=INTEGER}
WHERE id =
#{id,jdbcType=INTEGER}

UPDATE t_article
SET
status=#{status,jdbcType=INTEGER}
WHERE id =#{id,jdbcType=INTEGER}

mapper.select:
SELECT * FROM t_article
AND title like CONCAT(CONCAT('%', #{article.title,jdbcType=VARCHAR}), '%')
AND category_ids = #{article.categoryIds,jdbcType=INTEGER}
AND tag_ids like CONCAT(CONCAT('%', #{article.tagIds,jdbcType=VARCHAR}), '%')
AND status =#{article.status,jdbcType=VARCHAR}
ORDER BY write_time DESC
LIMIT #{pageConfig.pageIndex,jdbcType=INTEGER},#{pageConfig.pageSize,jdbcType=INTEGER}

SELECT COUNT(*) FROM t_article
AND title like CONCAT(CONCAT('%', #{article.title,jdbcType=VARCHAR}), '%')
AND category_ids = #{article.categoryIds,jdbcType=INTEGER}
AND tag_ids like CONCAT(CONCAT('%', #{article.tagIds,jdbcType=VARCHAR}), '%')
AND status =#{article.status,jdbcType=VARCHAR}

SELECT t1.*,t2.name as category_name,t2.code as category_code FROM t_article t1 LEFT JOIN t_category t2 ON t1.category_ids = t2.id
AND t1.title like CONCAT(CONCAT('%', #{article.title,jdbcType=VARCHAR}), '%')
AND t1.category_ids = #{article.categoryIds,jdbcType=INTEGER}
AND t1.tag_ids like CONCAT(CONCAT('%', #{article.tagIds,jdbcType=VARCHAR}), '%')
AND t1.status =#{article.status,jdbcType=VARCHAR}
ORDER BY t1.id DESC
LIMIT #{pageConfig.pageIndex,jdbcType=INTEGER},#{pageConfig.pageSize,jdbcType=INTEGER}

select t.*
	from t_article as t 
	where 
		DATE_FORMAT(t.pub_time,  #{archive_type,jdbcType=VARCHAR}) = #{month_name,jdbcType=VARCHAR}

			and status=#{status,jdbcType=INTEGER}  

	order by t.pub_time DESC;
